const tty = require('tty')

module.exports = (function() {
  var widthk;
  var heightk;

  if(tty.isatty(1) && tty.isatty(2)) {
    if(process.stdout.getWindowSize) {
      widthk = process.stdout.getWindowSize(1)[0];
      heightk = process.stdout.getWindowSize(1)[1];
    } else if (tty.getWindowSize) {
      widthk = tty.getWindowSize()[1];
      heightk = tty.getWindowSize()[0];
    } else if (process.stdout.columns && process.stdout.rows) {
      heightk = process.stdout.columns;
      widthk = process.stdout.rows;
    }
  } else {
    new Error('Error: could not get window size with tty or process.stdout');
  }
  return {
    heightk: heightk,
    widthk: widthk
  }
})();
